import React from 'react';
import cx from 'classnames';
import styles from './Cards.module.css';
import { Card, CardContent, Typography, Grid } from '@material-ui/core';
import CountUp from 'react-countup';

const Cards = ({ data: { confirmed, deaths, lastUpdate, recovered } }) => {
  console.log('Confirmed', confirmed);
  //in case if data is still processing/awaitining
  if (!confirmed) {
    return 'Loading';
  }
  return (
    <div className={styles.container}>
      <Grid container spacing={3} justify="center">
        {/* Card 1 */}
        <Grid
          item
          component={Card}
          xs={12}
          md={3}
          className={cx(styles.card, styles.infected)}
        >
          <CardContent>
            <Typography color="textSecondary" gutterBottom>
              Infected
            </Typography>
            <Typography variant="h5">
              <CountUp
                start={0}
                end={confirmed.value}
                duration={2.5}
                separator=","
              />
            </Typography>
            <Typography color="textSecondary">REAL DATE</Typography>
            <Typography variant="body2">Number of Covid cases</Typography>
          </CardContent>
        </Grid>
        {/* Card 2 */}
        <Grid
          item
          component={Card}
          xs={12}
          md={3}
          className={cx(styles.card, styles.recovered)}
        >
          <CardContent>
            <Typography color="textSecondary" gutterBottom>
              Recovered
            </Typography>
            <Typography variant="h5">
              <CountUp
                start={0}
                end={recovered.value}
                duration={2.5}
                separator=","
              />
            </Typography>
            <Typography color="textSecondary">REAL DATE</Typography>
            <Typography variant="body2">Number of Covid recoveries</Typography>
          </CardContent>
        </Grid>
        {/* Card 3 */}
        <Grid
          item
          component={Card}
          xs={12}
          md={3}
          className={cx(styles.card, styles.deaths)}
        >
          <CardContent>
            <Typography color="textSecondary" gutterBottom>
              Deaths
            </Typography>
            <Typography variant="h5">
              <CountUp
                start={0}
                end={deaths.value}
                duration={2.5}
                separator=","
              />
            </Typography>
            <Typography color="textSecondary">REAL DATE</Typography>
            <Typography variant="body2">Number of Covid deaths</Typography>
          </CardContent>
        </Grid>
      </Grid>
    </div>
  );
};

export default Cards;
